from scipy.optimize import minimizeimport numpy as npfrom astropy.io import fitsimport matplotlib.pyplot as pltfrom numpy.polynomial.polynomial import polyfitimport mathimport pandas as pd#*****************************Input values**********************************************Galaxies = pd.read_csv('../2_pipeline/0_Galaxies_Table/Galaxies_Table.csv')	FCC_index = Galaxies['FCC']g = Galaxies['g']r = Galaxies['r']i = Galaxies['i']u = Galaxies['u']Kinematics = pd.read_csv('../2_pipeline/1_Kinematics_Table/Kinematics_Table.csv')Dispersion = Kinematics['Sigma']#*****************************3d Data***********************************************fig =  plt.figure(figsize = (20,7))ax = fig.add_subplot(2,2,1)plt.scatter(g-r, Dispersion)plt.xlabel('$g-r$')plt.ylabel('$\sigma_{15}$')ax = fig.add_subplot(2,2,2)plt.scatter(u-g, Dispersion)plt.xlabel('$u-g$')plt.ylabel('$\sigma_{15}$')ax = fig.add_subplot(2,2,3)plt.scatter(r-i, Dispersion)plt.xlabel('$r-i$')plt.ylabel('$\sigma_{15}$')ax = fig.add_subplot(2,2,4)plt.scatter(i-g, Dispersion)plt.xlabel('$i-g$')plt.ylabel('$\sigma_{15}$')plt.savefig("../2_pipeline/3_color_Disp/Color_Disp.pdf")